    private void jButtonExecutarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonExecutarActionPerformed

        try {
            Pessoa objPessoa = new Pessoa();

            objPessoa.setNome(jTextFieldNome.getText());
            
            objPessoa.setPeso((jTextFieldPeso.getText().trim().length() == 0) ? 0
                    : Integer.parseInt(jTextFieldPeso.getText().trim()));

            objPessoa.setAltura((jTextFieldAltura.getText().trim().length() == 0) ? 0
                    : Float.parseFloat(jTextFieldAltura.getText().replace(",", ".").trim()));

            objPessoa.setSexo(jComboBoxSexo.getSelectedItem().toString());
            objPessoa.setImc();
            objPessoa.setSituacao();

            String saida = "NOME: " + objPessoa.getNome() + "\n"
                    + "PESO: " + String.format("%d", objPessoa.getPeso()) + " kg\n"
                    + "ALTURA: " + String.format("%.2f", objPessoa.getAltura()) + " m\n"
                    + "SEXO: " + objPessoa.getSexo() + "\n"
                    + "IMC: " + String.format("%.3f", objPessoa.getImc()) + "\n"
                    + "SITUAÇÃO: " + objPessoa.getSituacao();

            jTextAreaSaida.setText(saida);

        } catch (Exception e) {
            Erro erro = new Erro(e);
            JOptionPane.showMessageDialog(null, erro.getMensagem(),
                    "Opa! (" + erro.getCodigo() + ")", JOptionPane.ERROR_MESSAGE);
        }
    }//GEN-LAST:event_jButtonExecutarActionPerformed

    private void jTextFieldNomeKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_jTextFieldNomeKeyTyped

        int maximoCaracteres = 50;

        // FONTE: https://www.devmedia.com.br/jtextfield-aceitando-apenas-letras/26019
        Character caractere = evt.getKeyChar();
        if (!caractere.isLetter(evt.getKeyChar()) && caractere != ' ') { // Somente letras e espaço ;)
            evt.consume(); //elimina a ação do evento
        }

        if ((jTextFieldPeso.getText().length() >= maximoCaracteres)) {
            evt.consume();
            jTextFieldPeso.setText(jTextFieldPeso.getText().substring(0, maximoCaracteres));
        }
    }//GEN-LAST:event_jTextFieldNomeKeyTyped

    private void jTextFieldPesoKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_jTextFieldPesoKeyTyped

        int maximoCaracteres = 3;

        // FONTE: https://www.devmedia.com.br/jtextfield-aceitando-apenas-letras/26019
        String caracteres = "0987654321";// lista de caracters que não devem ser aceitos
        if (!caracteres.contains(evt.getKeyChar() + "")) {// se o character que gerou o evento não estiver na lista
            evt.consume();
        }

        if ((jTextFieldPeso.getText().length() >= maximoCaracteres)) {
            evt.consume();
            jTextFieldPeso.setText(jTextFieldPeso.getText().substring(0, maximoCaracteres));
        }
    }//GEN-LAST:event_jTextFieldPesoKeyTyped

    private void jTextFieldAlturaKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_jTextFieldAlturaKeyTyped

        int maximoCaracteres = 4;
        String texto = "";
        float valor = 0;

        // FONTE: https://www.devmedia.com.br/jtextfield-aceitando-apenas-letras/26019
        String caracteres = "0987654321";// lista de caracters que não devem ser aceitos
        if (!caracteres.contains(evt.getKeyChar() + "")) {
            evt.consume();
        }

        texto = jTextFieldAltura.getText();

        if ((texto.length() >= maximoCaracteres)) {
            evt.consume();
            jTextFieldAltura.setText(texto.substring(0, maximoCaracteres));
        }

        switch (texto.length()) {
            case 1:
                texto += ",";
                break;
            case 2:
                texto = texto.substring(0, 1);
                break;
        }

        jTextFieldAltura.setText(texto);

    }//GEN-LAST:event_jTextFieldAlturaKeyTyped

    private void jButtonLimparActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonLimparActionPerformed
        jTextFieldNome.setText("");
        jTextFieldPeso.setText("");
        jTextFieldAltura.setText("");
        jComboBoxSexo.setSelectedIndex(0);
        jTextAreaSaida.setText("");

        jTextFieldNome.grabFocus();
    }//GEN-LAST:event_jButtonLimparActionPerformed

